mapper.select:
select g.GOODS_NUM goodsNum, GOODS_NAME goodsName, GOODS_CONTENT goodsContent, GOODS_MAIN goodsMain, GOODS_MAIN_ORG goodsMainOrg , GOODS_IMAGE goodsImage, GOODS_IMAGE_ORG goodsImageOrg, DELIVERY_COST deliveryCost, MANUFACTURER manufacturer , VISIT_COUNT visitCount, MARKET_NUM marketNum, GOODS_KIND goodsKind , i.TOTAL_IPGO_QTY totalQty, i.goods_price goodsPrice from goods g left outer join(SELECT GOODS_NUM, SUM(IPGO_QTY) AS TOTAL_IPGO_QTY, MAX(GOODS_PRICE) AS GOODS_PRICE FROM GOODS_IPGO GROUP BY GOODS_NUM) i on g.goods_num = i.goods_num
where market_num = #{userNum}
order by g.goods_num desc

mapper.update:
update goods set GOODS_NAME=#{goodsName},GOODS_CONTENT=#{goodsContent}, GOODS_MAIN=#{goodsMain}, GOODS_MAIN_ORG=#{goodsMainOrg}
				, GOODS_IMAGE=#{goodsImage}, GOODS_IMAGE_ORG=#{goodsImageOrg}, DELIVERY_COST=#{deliveryCost}
				, MANUFACTURER=#{manufacturer}, GOODS_KIND=#{goodsKind}
where goods_num=#{goodsNum}
